import org.apache.spark.sql.{Column, DataFrame}

def modDF(df: DataFrame, columnName: String, values1: Array[(String, String)]):DataFrame  = {

    var df1:DataFrame = df
    for ((valUno,valDue) <- values1) {
        df1 = df1.withColumn(columnName, when(col(columnName)===valUno, valDue).otherwise(col(columnName)))
    }
    df1.withColumn(columnName, trim(col(columnName)))

}

